import 'package:cloud_firestore/cloud_firestore.dart';
class UserProfile{final String uid;final String nickname;final String email;final int level;final int nutriScore;UserProfile({required this.uid,required this.nickname,required this.email,this.level=1,this.nutriScore=0});factory UserProfile.fromFirestore(DocumentSnapshot doc){Map<String,dynamic>data=doc.data()as Map<String,dynamic>;return UserProfile(uid:doc.id,nickname:data['nickname']??'',email:data['email']??'',level:data['level']??1,nutriScore:data['nutriScore']??0);}Map<String,dynamic>toFirestore(){return{'nickname':nickname,'email':email,'level':level,'nutriScore':nutriScore};}}
