import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:provider/provider.dart';import 'package:myapp/core/providers/auth_provider.dart';
class DashboardScreen extends StatelessWidget{const DashboardScreen({super.key});@override Widget build(BuildContext context){final authProvider=context.watch<AuthProvider>();final userNickname=authProvider.userProfile?.nickname??'Nutri';return Scaffold(appBar:AppBar(title:const Text('Duelo Metabólico'),automaticallyImplyLeading:false,actions:[IconButton(icon:const Icon(Icons.person),tooltip:'Meu Perfil',onPressed:()=>context.push('/profile')),IconButton(icon:const Icon(Icons.menu_book),tooltip:'Manual do Jogo',onPressed:()=>context.push('/game-manual')),IconButton(icon:const Icon(Icons.logout),tooltip:'Sair',onPressed:()=>context.read<AuthProvider>().signOut())]),body:SingleChildScrollView(padding:const EdgeInsets.all(24.0),child:Column(crossAxisAlignment:CrossAxisAlignment.stretch,children:[const SizedBox(height:20),Text('Bem-vindo(a), $userNickname!',style:Theme.of(context).textTheme.headlineSmall,textAlign:TextAlign.center),const SizedBox(height:10),Text('Escolha seu modo de jogo para começar a duelar.',style:Theme.of(context).textTheme.bodyLarge,textAlign:TextAlign.center),const SizedBox(height:40),_buildGameModeCard(context,title:'Desafio Solo',description:'Pratique suas habilidades contra nossa IA.',icon:Icons.person_outline,onTap:()=>context.push('/solo-challenge')),const SizedBox(height:20),_buildGameModeCard(context,title:'Arena Online (Em Breve)',description:'Duele com nutricionistas do mundo inteiro.',icon:Icons.public,onTap:(){ScaffoldMessenger.of(context).showSnackBar(const SnackBar(content:Text('Modo online em desenvolvimento!')));})])));}}Widget _buildGameModeCard(BuildContext context,{required String title,required String description,required IconData icon,required VoidCallback onTap}){return Card(child:InkWell(onTap:onTap,borderRadius:BorderRadius.circular(16),child:Padding(padding:const EdgeInsets.all(24),child:Row(crossAxisAlignment:CrossAxisAlignment.start,children:[Icon(icon,size:40,color:Theme.of(context).primaryColor),const SizedBox(width:16),Expanded(child:Column(crossAxisAlignment:CrossAxisAlignment.start,children:[Text(title,style:Theme.of(context).textTheme.titleLarge),const SizedBox(height:8),Text(description,style:Theme.of(context).textTheme.bodyMedium)]))]))));}
