import 'package:flutter/material.dart';
class GameManualScreen extends StatelessWidget{const GameManualScreen({super.key});@override Widget build(BuildContext context){return Scaffold(appBar:AppBar(title:const Text('Manual do Jogo')),body:SingleChildScrollView(padding:const EdgeInsets.all(24.0),child:Column(crossAxisAlignment:CrossAxisAlignment.start,children:[Text('Conceitos Básicos',style:Theme.of(context).textTheme.headlineSmall),const SizedBox(height:16),_buildSection(context,title:'O que é o Duelo Metabólico?',content:'Um jogo educativo onde nutricionistas competem resolvendo casos clínicos. Você desenvolve estratégias nutricionais completas e ganha pontos pela qualidade da sua abordagem.')])));}}Widget _buildSection(BuildContext context,{required String title,required String content}){return Column(crossAxisAlignment:CrossAxisAlignment.start,children:[Text(title,style:Theme.of(context).textTheme.titleLarge),const SizedBox(height:8),Text(content,style:Theme.of(context).textTheme.bodyMedium)]);}
