import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:myapp/core/data/casos_clinicos_library.dart';
class MatchmakingScreen extends StatelessWidget{const MatchmakingScreen({super.key});@override Widget build(BuildContext context){return Scaffold(appBar:AppBar(title:const Text('Escolha seu Desafio Solo')),body:ListView.builder(padding:const EdgeInsets.all(16.0),itemCount:casosClinicos.length,itemBuilder:(context,index){final caso=casosClinicos[index];return Card(margin:const EdgeInsets.only(bottom:16.0),child:Padding(padding:const EdgeInsets.all(16.0),child:Column(crossAxisAlignment:CrossAxisAlignment.start,children:[Text(caso.title,style:Theme.of(context).textTheme.titleLarge),const SizedBox(height:8),Text(caso.queixaPrincipal,style:Theme.of(context).textTheme.bodyMedium),const SizedBox(height:16),ElevatedButton(onPressed:()=>context.push('/duel/${caso.id}'),child:const Text('Iniciar Duelo'))])));}));}}
